/* Описание и решаемые задачи БД:
 * База описывает упрощенную модель хранения данных интернет-магазина товаров для сахарной и восковой депиляции.
 * А именно: данные о товарах, клиентах, заказах, акциях, остатках на складах.
 * В базе хранятся личные данные о всех зарегистрированных клиентах интернет-магазина: их имя, фамилия, дата рождения,
 * контакты и хэш пароля.
 * Для удобстава, товары в интернет-магазине распределены на категории и бренды. Есть возможность посмореть заказы
 * и их состав для каждого клиента.
 * У клиентов есть статусы с соответствующей скидкой, которые будут присваиваться им в зависимости от общей суммы заказов.
 * Есть возможность сделать дополнительную скидку на определенный товар или конкретному клиенту магазина.
 * Также есть учет товароов и распределение их по складам.
 * Созданы представления и процедуры для удобной работы с данным.
 * А также триггеры, для того, чтобы защитить базу от действий, портящих ее данные.
 * (Все данные в таблицах внесены для примера и испытания работы скриптов, поэтому не являются достоверными)
 */


-- Создаем базу данных:

DROP DATABASE IF EXISTS depil_store;
CREATE DATABASE depil_store;
USE depil_store;

-- Создаем и заполняем таблицу статусов клиентов интернет-магазина:

DROP TABLE IF EXISTS client_statuses;
CREATE TABLE client_statuses (
	id SERIAL PRIMARY KEY, -- SERIAL = BIGINT UNSIGNED NOT NULL AUTO_INCREMENT UNIQUE
    statuses VARCHAR(50) COMMENT 'Статус клиента',
    client_discounts BIGINT COMMENT 'Личная скидка клиента',
    border BIGINT) COMMENT 'Граница перехода в новый статус';


-- Создаем и заполняем таблицу клиентов:

DROP TABLE IF EXISTS clients;
CREATE TABLE clients (
	id SERIAL PRIMARY KEY, -- SERIAL = BIGINT UNSIGNED NOT NULL AUTO_INCREMENT UNIQUE
    firstname VARCHAR(50) COMMENT 'Имя',
    lastname VARCHAR(50) COMMENT 'Фамилия',
    gender VARCHAR(6) COMMENT 'Пол',
    birthday_at DATE COMMENT 'Дата рождения',
    email VARCHAR(120) UNIQUE,
    phone BIGINT,
    password_hash VARCHAR(50),
    status_id BIGINT UNSIGNED COMMENT 'Статус клиента',
    created_at DATETIME DEFAULT CURRENT_TIMESTAMP,
    updated_at DATETIME DEFAULT CURRENT_TIMESTAMP ON UPDATE CURRENT_TIMESTAMP,
    FOREIGN KEY (status_id) REFERENCES client_statuses(id) ON UPDATE CASCADE ON DELETE SET NULL,
    INDEX clients_phone_idx(phone),
    INDEX clients_firstname_lastname_idx(firstname, lastname)
) COMMENT 'Покупатели';


-- Создаем и заполняем таблицу администратора интернет-магазина:

DROP TABLE IF EXISTS admin;
CREATE TABLE admin (
  id SERIAL PRIMARY KEY,
  acceses TEXT,
  FOREIGN KEY (id) REFERENCES clients(id) ON UPDATE CASCADE ON DELETE CASCADE
) COMMENT 'Администратор сайта';


-- Создаем и заполняем таблицу разделов интернет-магазина:

DROP TABLE IF EXISTS categories;
CREATE TABLE categories (
  id SERIAL PRIMARY KEY,
  name VARCHAR(255) COMMENT 'Название раздела',
  UNIQUE unique_name(name(10)), -- Все названия разделов должны быть разными
  INDEX categories_idx(name)
 ) COMMENT 'Разделы интернет-магазина';


 -- Создаем и заполняем таблицу брендов интернет-магазина:

DROP TABLE IF EXISTS brands;
CREATE TABLE brands (
  id SERIAL PRIMARY KEY,
  name VARCHAR(255) COMMENT 'Название бренда',
  UNIQUE unique_name(name(10)), -- Все названия брендов должны быть разными
  INDEX brands_idx(name)
) COMMENT 'Бренды, представленные в интернет-магазине';


 -- Создаем и заполняем таблицу товаров интернет-магазина:

DROP TABLE IF EXISTS products;
CREATE TABLE products (
  id SERIAL PRIMARY KEY,
  name VARCHAR(255) COMMENT 'Название',
  description TEXT COMMENT 'Описание',
  price DECIMAL (11,2) COMMENT 'Цена',
  brands_id BIGINT UNSIGNED COMMENT 'Бренд товара',
  categories_id BIGINT UNSIGNED COMMENT 'Категория товара',
  created_at DATETIME DEFAULT CURRENT_TIMESTAMP,
  updated_at DATETIME DEFAULT CURRENT_TIMESTAMP ON UPDATE CURRENT_TIMESTAMP,
  FOREIGN KEY (brands_id) REFERENCES brands(id) ON UPDATE CASCADE ON DELETE SET NULL, -- это не SERIAL, сделаем SET NULL
  FOREIGN KEY (categories_id) REFERENCES categories(id) ON UPDATE CASCADE ON DELETE SET NULL, -- если категории или бренды удалятся, то товары останутся
  INDEX index_of_brands_id (brands_id),
  INDEX index_of_categories_id (categories_id)
) COMMENT = 'Товары';

  ;

-- Создаем и заполняем таблицу заказов интернет-магазина:

DROP TABLE IF EXISTS orders;
CREATE TABLE orders (
  id SERIAL PRIMARY KEY,
  clients_id BIGINT UNSIGNED,
  created_at DATETIME DEFAULT CURRENT_TIMESTAMP,
  updated_at DATETIME DEFAULT CURRENT_TIMESTAMP ON UPDATE CURRENT_TIMESTAMP,
  FOREIGN KEY (clients_id) REFERENCES clients(id) ON UPDATE CASCADE ON DELETE CASCADE
) COMMENT = 'Заказы';


-- Создаем и заполняем таблицу составов заказов интернет-магазина:

DROP TABLE IF EXISTS orders_lists;
CREATE TABLE orders_lists (
  order_id BIGINT UNSIGNED,
  product_id BIGINT UNSIGNED,
  total BIGINT UNSIGNED DEFAULT 1 COMMENT 'Количество товаров в заказе',
  created_at DATETIME DEFAULT CURRENT_TIMESTAMP,
  updated_at DATETIME DEFAULT CURRENT_TIMESTAMP ON UPDATE CURRENT_TIMESTAMP,
  PRIMARY KEY(order_id, product_id),
  FOREIGN KEY (order_id) REFERENCES orders(id) ON UPDATE CASCADE ON DELETE CASCADE,
  FOREIGN KEY (product_id) REFERENCES products(id) ON UPDATE CASCADE ON DELETE CASCADE
) COMMENT 'Составы заказов';


-- Создаем и заполняем таблицу изменения цен товаров интернет-магазина:

DROP TABLE IF EXISTS discounts;
CREATE TABLE discounts (
  id SERIAL PRIMARY KEY,
  client_id BIGINT UNSIGNED,
  product_id BIGINT UNSIGNED,
  discount FLOAT UNSIGNED COMMENT 'Величина скидки от 0.0 до 1.0',
  started_at DATETIME,
  finished_at DATETIME,
  discriptions TEXT COMMENT 'Описание акции',
  created_at DATETIME DEFAULT CURRENT_TIMESTAMP,
  updated_at DATETIME DEFAULT CURRENT_TIMESTAMP ON UPDATE CURRENT_TIMESTAMP,
  FOREIGN KEY (client_id) REFERENCES clients(id) ON UPDATE CASCADE ON DELETE SET NULL, -- Если клиент удалится, то скидка останется, id клиента станет NULL
  FOREIGN KEY (product_id) REFERENCES products(id) ON UPDATE CASCADE ON DELETE CASCADE, -- Если товар удалится, то скидка тоже
  INDEX index_of_client_id(client_id),
  INDEX index_of_product_id(product_id)
) COMMENT 'Акции на товары';


-- Создаем и заполняем таблицу складов интернет-магазина:

DROP TABLE IF EXISTS storehouses;
CREATE TABLE storehouses (
  id SERIAL PRIMARY KEY,
  name VARCHAR(255) COMMENT 'Название',
  created_at DATETIME DEFAULT CURRENT_TIMESTAMP,
  updated_at DATETIME DEFAULT CURRENT_TIMESTAMP ON UPDATE CURRENT_TIMESTAMP
) COMMENT 'Склады';


-- Создаем и заполняем таблицу остатков на складах интернет-магазина:

DROP TABLE IF EXISTS remains;
CREATE TABLE remains (
  id SERIAL PRIMARY KEY,
  storehouse_id BIGINT UNSIGNED,
  product_id BIGINT UNSIGNED,
  value BIGINT UNSIGNED COMMENT 'Остаток товара на складе',
  created_at DATETIME DEFAULT CURRENT_TIMESTAMP,
  updated_at DATETIME DEFAULT CURRENT_TIMESTAMP ON UPDATE CURRENT_TIMESTAMP,
  FOREIGN KEY (storehouse_id) REFERENCES storehouses(id) ON UPDATE CASCADE ON DELETE CASCADE,
  FOREIGN KEY (product_id) REFERENCES products(id) ON UPDATE CASCADE ON DELETE CASCADE
) COMMENT 'Запасы на складе';


-- Создаем и заполняем таблицу фото интернет-магазина:

DROP TABLE IF EXISTS photos;
CREATE TABLE photos (
  id SERIAL PRIMARY KEY,
  photo_id BIGINT UNSIGNED,
  photo_name VARCHAR(255),
  metadata JSON,
  FOREIGN KEY (photo_id) REFERENCES products(id) ON UPDATE CASCADE ON DELETE CASCADE
) COMMENT 'Фотографии товаров';

